class callscreen extends StatelessWidget{
  Uint8List decodeBase64Image(String base64Image) {
    final base64String = base64Image.split(',').last;
    return base64Decode(base64String);
  }
  var arrContent = [
    {
      "images":
      "data:image/jpeg;base64,",
      "name": "Shayan",
      "lastmessage": "1 min ago",
      "time": "1 min ago",
      "noofmessages": "7",
    },
    {
      "images":
      "data:image/jpeg;base64,data:image/jpeg;base64,",
      "name": "Abdullah new jazz",
      "lastmessage": "55 min ago",
      "time": "04:05 am",
      "noofmessages": "3",
    },
    {
      "images":
      "data:image/jpeg;base64,data:image/jpeg;base64,",
      "name": "Api ",
      "lastmessage": "3 hours ago",
      "time": "3 hours ago",
      "noofmessages": "0",
    },



  ];
  List<Color> calliconcolor = [
    Colors.red,
    Colors.green,
    Colors.red,
  ];


  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Column(
        children: [
          const SizedBox(height: 30),
          Row(
            children:  [
              SizedBox(width: 10),
              UiHelper.CustomText(
                text: "Recent",
                size: 16,
                fontweight: FontWeight.w400,
              ),
            ],
          ),
          SizedBox(height: 20,),
          Expanded(
            child: ListView.builder(
              itemCount: arrContent.length,
              itemBuilder: (context, index) {
                var chat = arrContent[index];
                return ListTile(
                  leading: CircleAvatar(
                    backgroundImage:
                    MemoryImage(decodeBase64Image(chat["images"]!)),
                    radius: 25,
                  ),
                  title: Text(chat["name"] ?? '',style: TextStyle(fontSize: 17,fontWeight: FontWeight.w700),),
                  subtitle: Text(chat["lastmessage"] ?? '',style: TextStyle(fontSize: 15,fontWeight: FontWeight.w400)),
                  trailing: Icon(Icons.call,color: calliconcolor[index],),

                );
              },
            ),
          ),
        ],

      ),

    );
  }

}